import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';
import { useForm } from 'react-hook-form';
import { Plus, Search, Trash2, User, Mail, Phone, Eye } from 'lucide-react';
import ValidationErrors from '../../components/ValidationErrors';
import { useSocket } from '../../contexts/SocketContext';
import ConfirmationDialog from '../../components/ConfirmationDialog';

const Students = () => {
  const navigate = useNavigate();
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAddForm, setShowAddForm] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [validationErrors, setValidationErrors] = useState([]);
  const [deleteDialog, setDeleteDialog] = useState({ isOpen: false, studentId: null, studentName: '' });
  const { socket } = useSocket();

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset
  } = useForm({
    defaultValues: {
      name: '',
      email: '',
      password: '',
      phoneNumber: ''
    }
  });


  useEffect(() => {
    fetchStudents();
  }, []);

  // Real-time event listeners
  useEffect(() => {
    if (socket) {
      // Listen for new students
      socket.on('student-added', (data) => {
        console.log('üë®‚Äçüéì Student added:', data);
        // Refresh students list
        fetchStudents();
      });

      // Listen for deleted students
      socket.on('student-deleted', (data) => {
        console.log('üóëÔ∏è Student deleted:', data);
        // Refresh students list
        fetchStudents();
      });

      // Cleanup listeners on unmount
      return () => {
        socket.off('student-added');
        socket.off('student-deleted');
      };
    }
  }, [socket]);

  const fetchStudents = async () => {
    try {
      setLoading(true);
      const res = await axios.get('/api/users/students');
      setStudents(res.data.data);
    } catch (error) {
      console.error('Error fetching students:', error);
      toast.error('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ∑ŸÑÿßÿ®');
    } finally {
      setLoading(false);
    }
  };

  const onSubmit = async (data) => {
    try {
      console.log('=== CREATING STUDENT ===');
      console.log('Student data:', data);
      setValidationErrors([]);
      
      const response = await axios.post('/api/users/students', data);
      console.log('Student creation response:', response);
      console.log('Response status:', response.status);
      console.log('Response data:', response.data);
      
      toast.success('ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ∑ÿßŸÑÿ® ÿ®ŸÜÿ¨ÿßÿ≠');
      setShowAddForm(false);
      reset();
      fetchStudents();
    } catch (error) {
      console.error('=== ERROR CREATING STUDENT ===');
      console.error('Error object:', error);
      console.error('Error response:', error.response);
      console.error('Error message:', error.message);
      console.error('Error status:', error.response?.status);
      console.error('Error data:', error.response?.data);
      
      // Handle validation errors
      if (error.response?.data?.errors) {
        setValidationErrors(error.response.data.errors);
      } else if (error.response?.data?.message && error.response.data.message.includes('ŸäŸàÿ¨ÿØ ÿ∑ÿßŸÑÿ® ŸÖÿ≥ÿ¨ŸÑ ÿ®ÿßŸÑŸÅÿπŸÑ')) {
        // Handle duplicate user error specifically
        const errorMessage = error.response.data.message;
        toast.error(errorMessage);
        
        // Set validation error on the appropriate field
        const duplicateField = error.response.data.duplicateField;
        if (duplicateField === 'ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä') {
          setValidationErrors([{
            field: 'email',
            message: errorMessage
          }]);
        } else if (duplicateField === 'ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ') {
          setValidationErrors([{
            field: 'phoneNumber',
            message: errorMessage
          }]);
        } else {
          setValidationErrors([{
            field: 'email',
            message: errorMessage
          }]);
        }
        
        // Clear the form fields that have duplicates and show helpful message
        if (duplicateField === 'ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä') {
          reset({ email: '' });
          toast('Ÿäÿ±ÿ¨Ÿâ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿ®ÿ±ŸäÿØ ÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ŸÖÿÆÿ™ŸÑŸÅ', { icon: '‚ÑπÔ∏è' });
        } else if (duplicateField === 'ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ') {
          reset({ phoneNumber: '' });
          toast('Ÿäÿ±ÿ¨Ÿâ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿ±ŸÇŸÖ ÿ¨ŸàÿßŸÑ ŸÖÿÆÿ™ŸÑŸÅ', { icon: '‚ÑπÔ∏è' });
        }
      } else {
        toast.error(error.response?.data?.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ∑ÿßŸÑÿ®');
      }
    }
  };

  const handleDelete = (studentId, studentName) => {
    setDeleteDialog({
      isOpen: true,
      studentId: studentId,
      studentName: studentName
    });
  };

  const confirmDelete = async () => {
    try {
      await axios.delete(`/api/users/students/${deleteDialog.studentId}`);
      toast.success('ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑÿ∑ÿßŸÑÿ® ÿ®ŸÜÿ¨ÿßÿ≠');
      fetchStudents();
      setDeleteDialog({ isOpen: false, studentId: null, studentName: '' });
    } catch (error) {
      console.error('Error deleting student:', error);
      toast.error('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ≠ÿ∞ŸÅ ÿßŸÑÿ∑ÿßŸÑÿ®');
      setDeleteDialog({ isOpen: false, studentId: null, studentName: '' });
    }
  };

  const cancelDelete = () => {
    setDeleteDialog({ isOpen: false, studentId: null, studentName: '' });
  };

  const handleViewProfile = (studentId) => {
    navigate(`/teacher/students/${studentId}`);
  };


  const filteredStudents = students.filter(student =>
    student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
    (student.phoneNumber && student.phoneNumber.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="spinner"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ∑ŸÑÿßÿ®</h1>
          <p className="text-gray-600">ÿ•ÿ∂ÿßŸÅÿ© Ÿàÿ•ÿØÿßÿ±ÿ© ÿ≠ÿ≥ÿßÿ®ÿßÿ™ ÿßŸÑÿ∑ŸÑÿßÿ®</p>
        </div>
        <button
          onClick={() => setShowAddForm(true)}
          className="btn-primary flex items-center space-x-2 rtl:space-x-reverse"
        >
          <Plus className="h-4 w-4" />
          <span>ÿ•ÿ∂ÿßŸÅÿ© ÿ∑ÿßŸÑÿ®</span>
        </button>
      </div>

      {/* Search */}
      <div className="card">
        <div className="card-body">
          <div className="relative">
            <div className="absolute inset-y-0 right-0 pr-3 flex items-center pointer-events-none">
              <Search className="h-5 w-5 text-gray-400" />
            </div>
            <input
              type="text"
              placeholder="ÿßŸÑÿ®ÿ≠ÿ´ ŸÅŸä ÿßŸÑÿ∑ŸÑÿßÿ®..."
              className="input-field pr-10"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
      </div>

      {/* Add Student Form */}
      {showAddForm && (
        <div className="card">
          <div className="card-header">
            <h3 className="text-lg font-semibold text-gray-900">ÿ•ÿ∂ÿßŸÅÿ© ÿ∑ÿßŸÑÿ® ÿ¨ÿØŸäÿØ</h3>
          </div>
          <div className="card-body">
            <ValidationErrors 
              errors={validationErrors} 
              onClose={() => setValidationErrors([])} 
            />
            <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="form-group">
                  <label className="form-label">ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑŸÉÿßŸÖŸÑ</label>
                  <div className="relative">
                    <div className="absolute inset-y-0 right-0 pr-3 flex items-center pointer-events-none">
                      <User className="h-5 w-5 text-gray-400" />
                    </div>
                    <input
                      type="text"
                      className={`input-field pr-10 ${errors.name ? 'border-red-500' : ''}`}
                      placeholder="ÿ£ÿØÿÆŸÑ ÿßÿ≥ŸÖ ÿßŸÑÿ∑ÿßŸÑÿ®"
                      {...register('name', {
                        required: 'ÿßŸÑÿßÿ≥ŸÖ ŸÖÿ∑ŸÑŸàÿ®',
                        minLength: {
                          value: 2,
                          message: 'ÿßŸÑÿßÿ≥ŸÖ Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÉŸàŸÜ ÿ≠ÿ±ŸÅŸäŸÜ ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ'
                        }
                      })}
                    />
                  </div>
                  {errors.name && (
                    <p className="error-message">{errors.name.message}</p>
                  )}
                </div>

                <div className="form-group">
                  <label className="form-label">ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä</label>
                  <div className="relative">
                    <div className="absolute inset-y-0 right-0 pr-3 flex items-center pointer-events-none">
                      <Mail className="h-5 w-5 text-gray-400" />
                    </div>
                    <input
                      type="email"
                      className={`input-field pr-10 ${errors.email ? 'border-red-500' : ''}`}
                      placeholder="ÿ£ÿØÿÆŸÑ ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä"
                      {...register('email', {
                        required: 'ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ŸÖÿ∑ŸÑŸàÿ®',
                        pattern: {
                          value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                          message: 'ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠'
                        }
                      })}
                    />
                  </div>
                  {errors.email && (
                    <p className="error-message">{errors.email.message}</p>
                  )}
                </div>

                <div className="form-group">
                  <label className="form-label">ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±</label>
                  <input
                    type="password"
                    className={`input-field ${errors.password ? 'border-red-500' : ''}`}
                    placeholder="ÿ£ÿØÿÆŸÑ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±"
                    {...register('password', {
                      required: 'ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ŸÖÿ∑ŸÑŸàÿ®ÿ©',
                      minLength: {
                        value: 6,
                        message: 'ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± Ÿäÿ¨ÿ® ÿ£ŸÜ ÿ™ŸÉŸàŸÜ 6 ÿ£ÿ≠ÿ±ŸÅ ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ'
                      }
                    })}
                  />
                  {errors.password && (
                    <p className="error-message">{errors.password.message}</p>
                  )}
                </div>

                <div className="form-group">
                  <label className="form-label">ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ</label>
                  <div className="relative">
                    <div className="absolute inset-y-0 right-0 pr-3 flex items-center pointer-events-none">
                      <Phone className="h-5 w-5 text-gray-400" />
                    </div>
                    <input
                      type="tel"
                      className={`input-field pr-10 ${errors.phoneNumber ? 'border-red-500' : ''}`}
                      placeholder="ÿ£ÿØÿÆŸÑ ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ"
                      {...register('phoneNumber', {
                        required: 'ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ ŸÖÿ∑ŸÑŸàÿ®',
                        pattern: {
                          value: /^[0-9]{10,15}$/,
                          message: 'ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ Ÿäÿ¨ÿ® ÿ£ŸÜ Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ 10-15 ÿ±ŸÇŸÖ ŸÅŸÇÿ∑'
                        }
                      })}
                    />
                  </div>
                  {errors.phoneNumber && (
                    <p className="error-message">{errors.phoneNumber.message}</p>
                  )}
                </div>
              </div>

              <div className="flex justify-end space-x-3 rtl:space-x-reverse">
                <button
                  type="button"
                  onClick={() => {
                    setShowAddForm(false);
                    reset();
                  }}
                  className="btn-secondary"
                >
                  ÿ•ŸÑÿ∫ÿßÿ°
                </button>
                <button type="submit" className="btn-primary">
                  ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ∑ÿßŸÑÿ®
                </button>
              </div>
            </form>
          </div>
        </div>
      )}


      {/* Students List */}
      <div className="card">
        <div className="card-header">
          <h3 className="text-lg font-semibold text-gray-900">
            ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ∑ŸÑÿßÿ® ({filteredStudents.length})
          </h3>
        </div>
        <div className="card-body p-0">
          {filteredStudents.length === 0 ? (
            <div className="text-center py-8">
              <User className="h-12 w-12 text-gray-400 mx-auto mb-4" />
              <p className="text-gray-500">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ∑ŸÑÿßÿ® ŸÖÿ≥ÿ¨ŸÑŸäŸÜ</p>
            </div>
          ) : (
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gradient-to-r from-green-50 to-green-100">
                  <tr>
                    <th className="px-6 py-4 text-right text-sm font-semibold text-green-800 uppercase tracking-wider">
                      ÿßŸÑÿ∑ÿßŸÑÿ®
                    </th>
                    <th className="px-6 py-4 text-right text-sm font-semibold text-green-800 uppercase tracking-wider">
                      ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä
                    </th>
                    <th className="px-6 py-4 text-right text-sm font-semibold text-green-800 uppercase tracking-wider">
                      ÿ±ŸÇŸÖ ÿßŸÑÿ¨ŸàÿßŸÑ
                    </th>
                    <th className="px-6 py-4 text-center text-sm font-semibold text-green-800 uppercase tracking-wider">
                      ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {filteredStudents.map((student) => (
                    <tr key={student._id} className="hover:bg-green-50 transition-colors duration-200">
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center justify-end">
                          <div className="mr-4 text-right">
                            <div className="text-sm font-semibold text-gray-900">
                              {student.name}
                            </div>
                            <div className="text-xs text-gray-500">
                              {new Date(student.createdAt).toLocaleDateString('en-GB')}
                            </div>
                          </div>
                          <div className="h-10 w-10 flex-shrink-0">
                            <div className="h-10 w-10 rounded-full bg-gradient-to-r from-green-100 to-green-200 flex items-center justify-center shadow-sm">
                              <User className="h-5 w-5 text-green-600" />
                            </div>
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-right">
                        <div className="text-sm text-gray-900 font-medium">
                          {student.email}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-right">
                        <div className="text-sm text-gray-900 font-medium">
                          {student.phoneNumber || '-'}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-center">
                        <div className="flex items-center justify-center space-x-2 rtl:space-x-reverse">
                          <button
                            onClick={() => handleViewProfile(student._id)}
                            className="flex items-center space-x-1 rtl:space-x-reverse px-4 py-2 bg-gradient-to-r from-green-500 to-green-600 hover:from-green-600 hover:to-green-700 text-white rounded-lg transition-all duration-200 text-sm font-medium shadow-sm hover:shadow-md transform hover:-translate-y-0.5"
                            title="ÿπÿ±ÿ∂ ÿßŸÑŸÖŸÑŸÅ ÿßŸÑÿ¥ÿÆÿµŸä"
                          >
                            <Eye className="h-4 w-4" />
                            <span>ÿπÿ±ÿ∂</span>
                          </button>
                          <button
                            onClick={() => handleDelete(student._id, student.name)}
                            className="flex items-center space-x-1 rtl:space-x-reverse px-4 py-2 bg-gradient-to-r from-red-500 to-red-600 hover:from-red-600 hover:to-red-700 text-white rounded-lg transition-all duration-200 text-sm font-medium shadow-sm hover:shadow-md transform hover:-translate-y-0.5"
                            title="ÿ≠ÿ∞ŸÅ ÿßŸÑÿ∑ÿßŸÑÿ®"
                          >
                            <Trash2 className="h-4 w-4" />
                            <span>ÿ≠ÿ∞ŸÅ</span>
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Delete Confirmation Dialog */}
      <ConfirmationDialog
        isOpen={deleteDialog.isOpen}
        onClose={cancelDelete}
        onConfirm={confirmDelete}
        title="ÿ≠ÿ∞ŸÅ ÿßŸÑÿ∑ÿßŸÑÿ®"
        message={`ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∞ŸÅ ÿßŸÑÿ∑ÿßŸÑÿ® "${deleteDialog.studentName}"ÿü`}
        confirmText="ÿ≠ÿ∞ŸÅ ÿßŸÑÿ∑ÿßŸÑÿ®"
        cancelText="ÿ•ŸÑÿ∫ÿßÿ°"
        type="danger"
      />
    </div>
  );
};

export default Students;
