import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import { X, Eye, EyeOff, CheckCircle, XCircle, AlertCircle, BookOpen, Clock, TrendingUp, User, Award, Target, Zap, Star } from 'lucide-react';

const StudentExamSubmission = ({ studentId, studentName, examId, examTitle, onClose }) => {
  const [submission, setSubmission] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showAnswers, setShowAnswers] = useState(false);

  useEffect(() => {
    fetchSubmission();
  }, [studentId, examId]);

  const fetchSubmission = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`/api/exams/${examId}/student-submission/${studentId}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      setSubmission(response.data.data);
    } catch (error) {
      console.error('Error fetching submission:', error);
      toast.error('حدث خطأ أثناء تحميل إجابة الطالب');
      setSubmission(null);
    } finally {
      setLoading(false);
    }
  };

  const getAnswerStatus = (question, studentAnswer) => {
    if (studentAnswer === question.correctAnswer) {
      return { status: 'correct', icon: CheckCircle, color: 'text-green-600', bgColor: 'bg-green-100' };
    } else if (studentAnswer && studentAnswer !== question.correctAnswer) {
      return { status: 'wrong', icon: XCircle, color: 'text-red-600', bgColor: 'bg-red-100' };
    } else {
      return { status: 'unanswered', icon: AlertCircle, color: 'text-yellow-600', bgColor: 'bg-yellow-100' };
    }
  };

  const getAnswerLabel = (answer) => {
    const labels = { 'A': 'أ', 'B': 'ب', 'C': 'ج', 'D': 'د' };
    return labels[answer] || answer;
  };

  const getGroupName = (examGroup) => {
    return examGroup === 0 ? 'اختبارات التأسيس' : `المجموعة ${examGroup}`;
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed': return 'bg-green-100 text-green-800';
      case 'in_progress': return 'bg-yellow-100 text-yellow-800';
      case 'unlocked': return 'bg-blue-100 text-blue-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'completed': return 'مكتمل';
      case 'in_progress': return 'قيد التنفيذ';
      case 'unlocked': return 'متاح';
      default: return 'مقفل';
    }
  };

  if (loading) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg p-6 max-w-4xl w-full mx-4 max-h-[90vh] overflow-y-auto">
          <div className="flex justify-center items-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-green-600"></div>
            <span className="mr-3 text-gray-600">جاري التحميل...</span>
          </div>
        </div>
      </div>
    );
  }

  if (!submission) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg p-6 max-w-4xl w-full mx-4 max-h-[90vh] overflow-y-auto">
          <div className="text-center py-12">
            <AlertCircle className="mx-auto h-16 w-16 text-red-500 mb-4" />
            <h3 className="text-xl font-semibold text-gray-900 mb-2">لا توجد إجابة</h3>
            <p className="text-gray-600">الطالب لم يحاول هذا الامتحان بعد</p>
            <button
              onClick={onClose}
              className="mt-4 px-6 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
            >
              إغلاق
            </button>
          </div>
        </div>
      </div>
    );
  }

  const { exam, status, score, totalQuestions, percentage, answers, startTime, endTime } = submission;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-6xl w-full mx-4 max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex justify-between items-center mb-6 pb-4 border-b border-gray-200">
          <div>
            <h2 className="text-2xl font-bold text-gray-900">إجابة الطالب في الامتحان</h2>
            <p className="text-gray-600 mt-1">{examTitle}</p>
            <p className="text-sm text-gray-500 mt-1">{studentName}</p>
          </div>
          <div className="flex items-center space-x-4 space-x-reverse">
            <button
              onClick={() => setShowAnswers(!showAnswers)}
              className={`flex items-center px-3 py-2 rounded-lg text-sm font-medium transition-colors ${
                showAnswers 
                  ? 'bg-green-100 text-green-700 hover:bg-green-200' 
                  : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }`}
            >
              {showAnswers ? <EyeOff className="w-4 h-4 ml-2" /> : <Eye className="w-4 h-4 ml-2" />}
              {showAnswers ? 'إخفاء الإجابات الصحيحة' : 'إظهار الإجابات الصحيحة'}
            </button>
            <button
              onClick={onClose}
              className="p-2 text-gray-400 hover:text-gray-600 transition-colors"
            >
              <X className="w-6 h-6" />
            </button>
          </div>
        </div>

        {/* Exam Info */}
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="flex items-center">
              <BookOpen className="w-5 h-5 text-blue-600 ml-2" />
              <div>
                <div className="text-sm text-blue-600">المجموعة</div>
                <div className="font-semibold text-blue-900">{getGroupName(exam.examGroup)}</div>
              </div>
            </div>
            <div className="flex items-center">
              <TrendingUp className="w-5 h-5 text-green-600 ml-2" />
              <div>
                <div className="text-sm text-green-600">الدرجة</div>
                <div className="font-semibold text-green-900">{score}/{totalQuestions}</div>
              </div>
            </div>
            <div className="flex items-center">
              <CheckCircle className="w-5 h-5 text-purple-600 ml-2" />
              <div>
                <div className="text-sm text-purple-600">النسبة المئوية</div>
                <div className="font-semibold text-purple-900">{percentage}%</div>
              </div>
            </div>
            <div className="flex items-center">
              <Clock className="w-5 h-5 text-orange-600 ml-2" />
              <div>
                <div className="text-sm text-orange-600">الحالة</div>
                <div className="font-semibold text-orange-900">{getStatusText(status)}</div>
              </div>
            </div>
          </div>
        </div>

        {/* Questions */}
        <div className="space-y-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">الأسئلة والإجابات</h3>
          
          {exam.questions.map((question, index) => {
            const answer = answers.find(a => a.questionId.toString() === question._id.toString());
            const studentAnswer = answer ? answer.selectedAnswer : null;
            const answerStatus = getAnswerStatus(question, studentAnswer);
            const StatusIcon = answerStatus.icon;

            return (
              <div key={question._id} className="border border-gray-200 rounded-lg p-6 bg-gray-50">
                {/* Question Header */}
                <div className="flex items-center justify-between mb-4">
                  <div className="flex items-center">
                    <StatusIcon className={`w-6 h-6 ${answerStatus.color} ml-3`} />
                    <span className="text-lg font-semibold text-gray-900">
                      السؤال {index + 1}
                    </span>
                  </div>
                  <span className={`px-3 py-1 rounded-full text-sm font-medium ${answerStatus.bgColor} ${answerStatus.color}`}>
                    {answerStatus.status === 'correct' ? 'صحيح' : 
                     answerStatus.status === 'wrong' ? 'خطأ' : 'لم يُجِب'}
                  </span>
                </div>

                {/* Question Image */}
                <div className="mb-4">
                  <img
                    src={question.questionImage}
                    alt={`سؤال ${index + 1}`}
                    className="max-w-full h-auto rounded-lg border border-gray-200"
                    style={{ maxHeight: '400px' }}
                  />
                </div>

                {/* Answers */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {/* Student Answer */}
                  <div className="bg-white p-4 rounded-lg border border-gray-200">
                    <h4 className="font-semibold text-gray-900 mb-2 flex items-center">
                      <User className="w-4 h-4 ml-2" />
                      إجابة الطالب
                    </h4>
                    <div className={`inline-flex items-center px-3 py-2 rounded-lg font-medium ${
                      answerStatus.status === 'correct' 
                        ? 'bg-green-100 text-green-800'
                        : answerStatus.status === 'wrong'
                        ? 'bg-red-100 text-red-800'
                        : 'bg-yellow-100 text-yellow-800'
                    }`}>
                      {studentAnswer ? getAnswerLabel(studentAnswer) : 'لم يُجِب'}
                    </div>
                  </div>

                  {/* Correct Answer (if showAnswers is true) */}
                  {showAnswers && (
                    <div className="bg-white p-4 rounded-lg border border-gray-200">
                      <h4 className="font-semibold text-gray-900 mb-2 flex items-center">
                        <CheckCircle className="w-4 h-4 ml-2" />
                        الإجابة الصحيحة
                      </h4>
                      <div className="inline-flex items-center px-3 py-2 rounded-lg font-medium bg-green-100 text-green-800">
                        {getAnswerLabel(question.correctAnswer)}
                      </div>
                    </div>
                  )}
                </div>

                {/* Explanation */}
                {question.explanation && showAnswers && (
                  <div className="mt-4 bg-blue-50 p-4 rounded-lg border border-blue-200">
                    <h4 className="font-semibold text-blue-900 mb-2">التفسير:</h4>
                    <p className="text-blue-800">{question.explanation}</p>
                  </div>
                )}
              </div>
            );
          })}
        </div>

        {/* Footer */}
        <div className="flex justify-between items-center mt-6 pt-4 border-t border-gray-200">
          <div className="text-sm text-gray-500">
            {startTime && (
              <div>وقت البداية: {new Date(startTime).toLocaleString('ar-SA')}</div>
            )}
            {endTime && (
              <div>وقت الانتهاء: {new Date(endTime).toLocaleString('ar-SA')}</div>
            )}
          </div>
          <button
            onClick={onClose}
            className="px-6 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition-colors"
          >
            إغلاق
          </button>
        </div>
      </div>
    </div>
  );
};

export default StudentExamSubmission;
