import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import { Users, BookOpen, BarChart3, TrendingUp, CheckCircle } from 'lucide-react';
import { useSocket } from '../../contexts/SocketContext';
import { useAuth } from '../../context/AuthContext';

const TeacherDashboard = () => {
  const [stats, setStats] = useState({
    totalStudents: 0,
    totalExams: 0,
    completedExams: 0,
    averageScore: 0
  });
  const [loading, setLoading] = useState(true);
  const { socket } = useSocket();
  const { isAuthenticated, token } = useAuth();

  useEffect(() => {
    // Only fetch stats when user is authenticated and token is available
    if (isAuthenticated && token) {
      fetchDashboardStats();
    }
  }, [isAuthenticated, token]);

  // Real-time event listeners
  useEffect(() => {
    if (socket) {
      // Listen for exam submissions
      socket.on('exam-submitted', (data) => {
        console.log('üìù Exam submitted:', data);
        toast.success(`ÿ™ŸÖ ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÖÿ™ÿ≠ÿßŸÜ: ${data.studentName} - ${data.examTitle} (${data.percentage}%)`);
        // Refresh dashboard stats
        fetchDashboardStats();
      });

      // Listen for new students
      socket.on('student-added', (data) => {
        console.log('üë®‚Äçüéì Student added:', data);
        toast.success(`ÿ™ŸÖ ÿ•ÿ∂ÿßŸÅÿ© ÿ∑ÿßŸÑÿ® ÿ¨ÿØŸäÿØ: ${data.studentName}`);
        // Refresh dashboard stats
        fetchDashboardStats();
      });

      // Listen for deleted students
      socket.on('student-deleted', (data) => {
        console.log('üóëÔ∏è Student deleted:', data);
        toast.success(`ÿ™ŸÖ ÿ≠ÿ∞ŸÅ ÿßŸÑÿ∑ÿßŸÑÿ®: ${data.studentName}`);
        // Refresh dashboard stats
        fetchDashboardStats();
      });

      // Cleanup listeners on unmount
      return () => {
        socket.off('exam-submitted');
        socket.off('student-added');
        socket.off('student-deleted');
      };
    }
  }, [socket]);

  const fetchDashboardStats = async () => {
    try {
      setLoading(true);
      console.log('üîç Fetching dashboard stats...');
      console.log('üîë Auth token:', localStorage.getItem('token'));
      
      const [studentsRes, examsRes] = await Promise.all([
        axios.get('/api/users/students'),
        axios.get('/api/exams')
      ]);
      
      console.log('‚úÖ API responses received:', { studentsRes, examsRes });

      const students = studentsRes.data.data;
      const exams = examsRes.data.data;

      // Calculate stats
      const totalStudents = students.length;
      const totalExams = exams.length;
      
      // Calculate completed exams and average score
      let completedExams = 0;
      let totalScore = 0;
      let totalAttempts = 0;

      students.forEach(student => {
