import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';
import { 
  ArrowLeft, 
  User, 
  Mail, 
  Hash, 
  BookOpen, 
  CheckCircle, 
  XCircle, 
  Clock,
  Target,
  Filter,
  Play
} from 'lucide-react';

const StudentProfile = () => {
  const { studentId } = useParams();
  const navigate = useNavigate();
  const [student, setStudent] = useState(null);
  const [exams, setExams] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showExamSelection, setShowExamSelection] = useState(false);
  const [showCategorySelection, setShowCategorySelection] = useState(false);
  const [selectedExams, setSelectedExams] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);

  useEffect(() => {
    fetchStudentData();
    fetchExams();
  }, [studentId]);

  const fetchStudentData = async () => {
    try {
      const response = await axios.get(`/api/users/students/${studentId}`);
      setStudent(response.data.data);
    } catch (error) {
      console.error('Error fetching student:', error);
      toast.error('حدث خطأ أثناء تحميل بيانات الطالب');
      navigate('/teacher/students');
    }
  };

  const fetchExams = async () => {
    try {
      const response = await axios.get('/api/exams');
      setExams(response.data.data);
    } catch (error) {
      console.error('Error fetching exams:', error);
      toast.error('حدث خطأ أثناء تحميل الامتحانات');
    } finally {
      setLoading(false);
    }
  };

  const handleRepeatExam = async (examId) => {
    try {
      await axios.post(`/api/exams/${examId}/repeat`, { studentId });
      toast.success('تم إعادة تعيين الامتحان للطالب');
      fetchStudentData(); // Refresh student data
    } catch (error) {
      console.error('Error repeating exam:', error);
      toast.error('حدث خطأ أثناء إعادة تعيين الامتحان');
    }
  };

  const handleAssignSpecificExams = async () => {
    try {
      await axios.post(`/api/users/students/${studentId}/assign-exams`, {
        examIds: selectedExams
      });
      toast.success('تم تعيين الامتحانات المحددة للطالب');
      setShowExamSelection(false);
      setSelectedExams([]);
      fetchStudentData();
    } catch (error) {
      console.error('Error assigning exams:', error);
      toast.error('حدث خطأ أثناء تعيين الامتحانات');
    }
  };

  const handleAssignCategories = async () => {
    try {
      // Assign each selected category
      for (const category of selectedCategories) {
        await axios.post(`/api/users/students/${studentId}/assign-category`, {
          category: parseInt(category)
        });
      }
      toast.success(`تم تعيين ${selectedCategories.length} مجموعة للطالب`);
      setShowCategorySelection(false);
      setSelectedCategories([]);
      fetchStudentData();
    } catch (error) {
      console.error('Error assigning categories:', error);
      toast.error('حدث خطأ أثناء تعيين المجموعات');
    }
  };


  const getProgressPercentage = (progress) => {
    if (!progress || !progress.totalQuestions) return 0;
    return Math.round((progress.correctAnswers / progress.totalQuestions) * 100);
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'completed': return 'text-green-600 bg-green-100';
      case 'in_progress': return 'text-yellow-600 bg-yellow-100';
      case 'not_started': return 'text-gray-600 bg-gray-100';
      default: return 'text-gray-600 bg-gray-100';
    }
  };

  const getStatusText = (status) => {
    switch (status) {
      case 'completed': return 'مكتمل';
      case 'in_progress': return 'قيد التنفيذ';
      case 'not_started': return 'لم يبدأ';
      default: return 'غير محدد';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="spinner"></div>
      </div>
    );
  }

  if (!student) {
    return (
      <div className="text-center py-12">
        <h2 className="text-xl font-semibold text-gray-900 mb-4">الطالب غير موجود</h2>
        <button
          onClick={() => navigate('/teacher/students')}
          className="btn-primary"
        >
          العودة لقائمة الطلاب
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto space-y-6">
      {/* Header */}
      <div className="card">
        <div className="card-body">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4 rtl:space-x-reverse">
              <button
                onClick={() => navigate('/teacher/students')}
                className="flex items-center space-x-2 rtl:space-x-reverse text-gray-600 hover:text-gray-800 transition-colors"
              >
                <ArrowLeft className="h-4 w-4" />
                <span>العودة</span>
              </button>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">ملف الطالب</h1>
                <p className="text-gray-600">تتبع تقدم الطالب وإدارة امتحاناته</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Student Info */}
      <div className="card">
        <div className="card-header">
          <h3 className="text-lg font-semibold text-gray-900">معلومات الطالب</h3>
        </div>
        <div className="card-body">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="flex items-center space-x-3 rtl:space-x-reverse">
              <div className="h-12 w-12 rounded-full bg-primary-100 flex items-center justify-center">
                <User className="h-6 w-6 text-primary-600" />
              </div>
              <div>
                <p className="text-sm font-medium text-gray-500">الاسم</p>
                <p className="text-lg font-semibold text-gray-900">{student.name}</p>
              </div>
            </div>
            <div className="flex items-center space-x-3 rtl:space-x-reverse">
              <div className="h-12 w-12 rounded-full bg-blue-100 flex items-center justify-center">
                <Mail className="h-6 w-6 text-blue-600" />
              </div>
              <div>
                <p className="text-sm font-medium text-gray-500">البريد الإلكتروني</p>
                <p className="text-lg font-semibold text-gray-900">{student.email}</p>
              </div>
            </div>
            <div className="flex items-center space-x-3 rtl:space-x-reverse">
              <div className="h-12 w-12 rounded-full bg-green-100 flex items-center justify-center">
                <Hash className="h-6 w-6 text-green-600" />
              </div>
              <div>
                <p className="text-sm font-medium text-gray-500">رقم الجوال</p>
                <p className="text-lg font-semibold text-gray-900">{student.phoneNumber || 'غير محدد'}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="card">
        <div className="card-header">
          <h3 className="text-lg font-semibold text-gray-900">إدارة الامتحانات</h3>
          <p className="text-sm text-gray-600 mt-1">تعيين امتحانات محددة أو مجموعات كاملة للطالب</p>
        </div>
        <div className="card-body">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Assign Specific Exams */}
            <div className="bg-gradient-to-br from-blue-50 to-blue-100 border border-blue-200 rounded-lg p-6 hover:shadow-md transition-shadow">
              <div className="flex items-center space-x-3 rtl:space-x-reverse mb-4">
                <div className="h-12 w-12 rounded-full bg-blue-500 flex items-center justify-center">
                  <Target className="h-6 w-6 text-white" />
                </div>
                <div>
                  <h4 className="text-lg font-semibold text-blue-900">تعيين امتحانات محددة</h4>
                  <p className="text-sm text-blue-700">اختر امتحانات معينة من قائمة الامتحانات المتاحة</p>
                </div>
              </div>
              <button
                onClick={() => setShowExamSelection(true)}
                className="w-full bg-blue-600 hover:bg-blue-700 text-white px-4 py-3 rounded-lg font-medium transition-colors flex items-center justify-center space-x-2 rtl:space-x-reverse"
              >
                <Target className="h-4 w-4" />
                <span>اختيار الامتحانات</span>
              </button>
            </div>

            {/* Assign Category */}
            <div className="bg-gradient-to-br from-green-50 to-green-100 border border-green-200 rounded-lg p-6 hover:shadow-md transition-shadow">
              <div className="flex items-center space-x-3 rtl:space-x-reverse mb-4">
                <div className="h-12 w-12 rounded-full bg-green-500 flex items-center justify-center">
                  <Filter className="h-6 w-6 text-white" />
                </div>
                <div>
                  <h4 className="text-lg font-semibold text-green-900">تعيين مجموعة محددة</h4>
                  <p className="text-sm text-green-700">اختر مجموعة كاملة (1-8) لتعيين جميع امتحاناتها</p>
                </div>
              </div>
              <button
                onClick={() => setShowCategorySelection(true)}
                className="w-full bg-green-600 hover:bg-green-700 text-white px-4 py-3 rounded-lg font-medium transition-colors flex items-center justify-center space-x-2 rtl:space-x-reverse"
              >
                <Filter className="h-4 w-4" />
                <span>اختيار المجموعة</span>
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Exam Progress */}
      <div className="card">
        <div className="card-header">
          <h3 className="text-lg font-semibold text-gray-900">تقدم الطالب في الامتحانات</h3>
        </div>
        <div className="card-body p-0">
          {student.examProgress && student.examProgress.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-200">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                      الامتحان
                    </th>
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                      المجموعة
                    </th>
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                      الحالة
                    </th>
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                      النتيجة
                    </th>
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                      التقدم
                    </th>
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                      الإجراءات
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {student.examProgress.map((progress, index) => {
                    const exam = exams.find(e => e._id === progress.examId);
                    if (!exam) return null;
                    
                    return (
                      <tr key={progress.examId} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="flex items-center">
                            <div className="h-10 w-10 flex-shrink-0">
                              <div className="h-10 w-10 rounded-full bg-primary-100 flex items-center justify-center">
                                <BookOpen className="h-5 w-5 text-primary-600" />
                              </div>
                            </div>
                            <div className="mr-4">
                              <div className="text-sm font-medium text-gray-900">
                                {exam.title}
                              </div>
                              <div className="text-sm text-gray-500">
                                {progress.totalQuestions} أسئلة
                              </div>
                            </div>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          المجموعة {exam.examGroup}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`inline-flex px-2 py-1 text-xs font-semibold rounded-full ${getStatusColor(progress.status)}`}>
                            {getStatusText(progress.status)}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {progress.status === 'completed' ? (
                            <div className="flex items-center space-x-2 rtl:space-x-reverse">
                              <CheckCircle className="h-4 w-4 text-green-500" />
                              <span>{progress.correctAnswers}/{progress.totalQuestions}</span>
                              <span className="text-green-600 font-semibold">
                                ({getProgressPercentage(progress)}%)
                              </span>
                            </div>
                          ) : progress.status === 'in_progress' ? (
                            <div className="flex items-center space-x-2 rtl:space-x-reverse">
                              <Clock className="h-4 w-4 text-yellow-500" />
                              <span>قيد التنفيذ</span>
                            </div>
                          ) : (
                            <div className="flex items-center space-x-2 rtl:space-x-reverse">
                              <XCircle className="h-4 w-4 text-gray-400" />
                              <span>لم يبدأ</span>
                            </div>
                          )}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="w-full bg-gray-200 rounded-full h-2">
                            <div 
                              className="bg-primary-600 h-2 rounded-full transition-all duration-300"
                              style={{ width: `${getProgressPercentage(progress)}%` }}
                            ></div>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                          {progress.status === 'completed' && (
                            <button
                              onClick={() => handleRepeatExam(progress.examId)}
                              className="text-primary-600 hover:text-primary-900 flex items-center space-x-1 rtl:space-x-reverse"
                              title="إعادة تعيين الامتحان"
                            >
                              <Play className="h-4 w-4" />
                              <span>إعادة تعيين</span>
                            </button>
                          )}
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-8">
              <BookOpen className="mx-auto h-12 w-12 text-gray-400" />
              <h3 className="mt-2 text-sm font-medium text-gray-900">لا توجد امتحانات</h3>
              <p className="mt-1 text-sm text-gray-500">لم يتم تعيين أي امتحانات لهذا الطالب بعد.</p>
            </div>
          )}
        </div>
      </div>

      {/* Exam Selection Modal */}
      {showExamSelection && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50 flex items-center justify-center p-4">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-2xl max-h-[90vh] overflow-hidden">
            <div className="bg-blue-600 text-white px-6 py-4">
              <div className="flex items-center space-x-3 rtl:space-x-reverse">
                <Target className="h-6 w-6" />
                <div>
                  <h3 className="text-lg font-semibold">اختيار الامتحانات المحددة</h3>
                  <p className="text-blue-100 text-sm">اختر الامتحانات التي تريد تعيينها للطالب</p>
                </div>
              </div>
            </div>
            <div className="p-6">
              <div className="max-h-96 overflow-y-auto space-y-3">
                {exams.length > 0 ? (
                  exams.map((exam) => (
                    <label key={exam._id} className="flex items-center space-x-3 rtl:space-x-reverse p-4 border border-gray-200 rounded-lg hover:bg-blue-50 hover:border-blue-300 transition-colors cursor-pointer">
                      <input
                        type="checkbox"
                        checked={selectedExams.includes(exam._id)}
                        onChange={(e) => {
                          if (e.target.checked) {
                            setSelectedExams([...selectedExams, exam._id]);
                          } else {
                            setSelectedExams(selectedExams.filter(id => id !== exam._id));
                          }
                        }}
                        className="h-4 w-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                      />
                      <div className="flex-1">
                        <div className="text-sm font-medium text-gray-900">{exam.title}</div>
                        <div className="text-sm text-gray-500 mt-1">
                          المجموعة {exam.examGroup} • {exam.questions.length} أسئلة
                        </div>
                      </div>
                    </label>
                  ))
                ) : (
                  <div className="text-center py-8">
                    <BookOpen className="mx-auto h-12 w-12 text-gray-400" />
                    <p className="text-gray-500 mt-2">لا توجد امتحانات متاحة</p>
                  </div>
                )}
              </div>
              <div className="flex justify-between items-center mt-6 pt-4 border-t border-gray-200">
                <div className="text-sm text-gray-600">
                  {selectedExams.length > 0 && `تم اختيار ${selectedExams.length} امتحان`}
                </div>
                <div className="flex space-x-3 rtl:space-x-reverse">
                  <button
                    onClick={() => {
                      setShowExamSelection(false);
                      setSelectedExams([]);
                    }}
                    className="px-4 py-2 text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-lg font-medium transition-colors"
                  >
                    إلغاء
                  </button>
                  <button
                    onClick={handleAssignSpecificExams}
                    disabled={selectedExams.length === 0}
                    className="px-4 py-2 bg-blue-600 hover:bg-blue-700 disabled:bg-gray-300 disabled:cursor-not-allowed text-white rounded-lg font-medium transition-colors"
                  >
                    تعيين الامتحانات المحددة ({selectedExams.length})
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Category Selection Modal */}
      {showCategorySelection && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50 flex items-center justify-center p-4">
          <div className="bg-white rounded-lg shadow-xl w-full max-w-md">
            <div className="bg-green-600 text-white px-6 py-4">
              <div className="flex items-center space-x-3 rtl:space-x-reverse">
                <Filter className="h-6 w-6" />
                <div>
                  <h3 className="text-lg font-semibold">اختيار المجموعة</h3>
                  <p className="text-green-100 text-sm">اختر مجموعة لتعيين جميع امتحاناتها للطالب</p>
                </div>
              </div>
            </div>
            <div className="p-6">
              <div className="grid grid-cols-2 gap-3">
                {Array.from({ length: 8 }, (_, i) => i + 1).map((groupNum) => (
                  <label key={groupNum} className="flex items-center space-x-3 rtl:space-x-reverse p-4 border border-gray-200 rounded-lg hover:bg-green-50 hover:border-green-300 transition-colors cursor-pointer">
                    <input
                      type="radio"
                      name="category"
                      value={groupNum}
                      checked={selectedCategory === groupNum.toString()}
                      onChange={(e) => setSelectedCategory(e.target.value)}
                      className="h-4 w-4 text-green-600 border-gray-300 focus:ring-green-500"
                    />
                    <div className="flex items-center space-x-2 rtl:space-x-reverse">
                      <div className="h-8 w-8 rounded-full bg-green-100 flex items-center justify-center">
                        <span className="text-sm font-semibold text-green-700">{groupNum}</span>
                      </div>
                      <span className="text-sm font-medium text-gray-900">المجموعة {groupNum}</span>
                    </div>
                  </label>
                ))}
              </div>
              <div className="flex justify-between items-center mt-6 pt-4 border-t border-gray-200">
                <div className="text-sm text-gray-600">
                  {selectedCategory && `تم اختيار المجموعة ${selectedCategory}`}
                </div>
                <div className="flex space-x-3 rtl:space-x-reverse">
                  <button
                    onClick={() => {
                      setShowCategorySelection(false);
                      setSelectedCategory('');
                    }}
                    className="px-4 py-2 text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-lg font-medium transition-colors"
                  >
                    إلغاء
                  </button>
                  <button
                    onClick={handleAssignCategory}
                    disabled={!selectedCategory}
                    className="px-4 py-2 bg-green-600 hover:bg-green-700 disabled:bg-gray-300 disabled:cursor-not-allowed text-white rounded-lg font-medium transition-colors"
                  >
                    تعيين المجموعة {selectedCategory}
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

    </div>
  );
};

export default StudentProfile;
